import { gql } from 'apollo-boost';

const QUERY_PATIENTS = gql`
    query {
        patients {
            id
            firstName
            lastName
            age
            gender
            avatar
            contactInfo
            conditions {
                id
                name
                severity
                startDate
                endDate
            }
        }
    }`;
