import React from 'react';
import { useQuery, useMutation } from '@apollo/client';
import { QUERY_PATIENTS, CREATE_PATIENT } from './queries.js'

export function PatientInfo() {

    const { data, loading } = useQuery(
        QUERY_PATIENTS
    );

    if (loading) return <p>Loading...</p>;

    return data.patients.map(({ id, firstName, lastName, age, contactInfo, gender, avatar }) => (
        <div key={id} className={id}>
            <p>
                Patient - {id}: {firstName} {lastName}
            </p>

            <button style={{marginRight: "10px" }} className="btn btn-primary" type="button" data-toggle="collapse" data-target={"#collapse"+id} aria-expanded="false" aria-controls={"collapse"+id}>Details</button>
            <a href={ "/patients/" + id } ><button style={{marginLeft: "10px" }} className="row btn btn-primary">View Patient</button></a>

            <div className="collapse multi-collapse" id={"collapse"+id}>
                <div className="card card-body">
                    <ul style={{listStyleType: 'none' }}>
                        <li>Age: { age }</li>
                        <li>Gender: { gender }</li>
                        <li>Contact: { contactInfo }</li>
                    </ul>
                </div>
            </div>
        </div>
    ));
}

export function CreatePatient() {
    let firstName, lastName, age, gender, contactInfo, condition_name, condition_severity, startDate, endDate;
    const [createPatient] = useMutation(CREATE_PATIENT);

    return (
        <div>
        <form
            onSubmit={e => {
            e.preventDefault();

            createPatient({ variables: {
                firstName: firstName.value,
                lastName: lastName.value,
                age: age.value,
                contactInfo: contactInfo.value,
                gender: gender.value,
                conditions: {
                    name: condition_name.value,
                    severity: condition_severity.value,
                    startDate: startDate.value,
                    endDate: endDate.value,
                },
            } });

            window.location.reload();
        }}
        style = {{ marginTop: '2em', marginBottom: '2em' }}>
            <button style={{marginRight: "10px" }} className="btn btn-primary" type="button" data-toggle="collapse" data-target={"#collapseInput"} aria-expanded="false" aria-controls={"collapseInput"}>Details</button>

            <div className="collapseInput">
                <input placeholder="First Name"
                    ref={node => {
                        firstName = node;
                    }}
                    style={{ marginRight: '1em' }}
                />

                <input placeholder="Last Name"
                    ref={node => {
                        lastName = node;
                    }}
                    style={{ marginRight: '1em' }}
                />

                <input placeholder="Email"
                    ref={node => {
                        contactInfo = node;
                    }}
                    style={{ marginRight: '1em' }}
                />

                <input placeholder="Gender"
                    ref={node => {
                        gender = node;
                    }}
                    style={{ marginRight: '1em' }}
                />

                <input placeholder="Age"
                    ref={node => {
                        age = node;
                    }}
                    style={{ marginRight: '1em' }}
                />

                <div class="form-group">
                    <label for="FormControlSelect" style={{ fontWeight: 'bold', paddingTop: '5px' }}>Conditions</label>
                    <select multiple class="form-control" id="FormControlSelect" ref={node => {
                        condition_name = node;
                    }}>
                        <option>Alzheimer’s disease</option>
                        <option>Arthritis</option>
                        <option>Asthma</option>
                        <option>Cancer</option>
                        <option>Diabetes</option>
                        <option>Heart disease</option>
                        <option>High blood pressure</option>
                        <option>High Cholesterol</option>
                        <option>Lower back pain</option>
                        <option>Migraine headaches</option>
                        <option>Stroke</option>
                    </select>
                </div>

                <div class="form-group">
                    <label for="FormControlSelect">Severity</label>
                    <select class="form-control" id="FormControlSelect" ref={node => {
                        condition_severity = node;
                    }}>
                        <option>1</option>
                        <option>2</option>
                        <option>3</option>
                    </select>
                </div>


                <input placeholder="Start Date"
                    ref={node => {
                        age = node;
                    }}
                    style={{ marginRight: '1em' }}
                />

                <input placeholder="End Date"
                    ref={node => {
                        age = node;
                    }}
                    style={{ marginRight: '1em' }}
                />

                <button className={"btn btn-primary"} type="submit" style={{ cursor: 'pointer' }}>Add to Database</button>
            </div>
        </form>
    </div>
    );}
