import React from "react";
import {useParams} from "react-router-dom";
import { useQuery, useMutation } from '@apollo/client';
import { QUERY_SINGLE_PATIENT } from './queries.js';

export function DetailPatient () {
    const {id} = useParams()

    const { data, loading } = useQuery(
        QUERY_SINGLE_PATIENT, { variables: { id: id } }
    );

    if (loading) return <p>Loading...</p>;

    console.log(typeof data.patientById.conditions)

    return data.patientById.conditions.map(function(condition) { 
        <div>
            <h2>{data.patientById.firstName}</h2>

            <ul style={{listStyleType:'none'}}>
                <li>{data.patientById.id}</li>
                <li>{data.patientById.lastName}</li>
                <li>{data.patientById.age}</li>
                <li>{data.patientById.contactInfo}</li>
                <li>{data.patientById.gender}</li>
            </ul>

            <h3>Conditions</h3>
            <div key={condition.d} className={condition.id}>
                <ul>
                    <li>{ id }</li>
                    <li>{condition.conditionName}</li>
                    <li>{condition.severity}</li>
                    <li>{condition.startDate}</li>
                    <li>{condition.endDate}</li>
                </ul>
            </div>
        </div>
    });
};
