import { gql } from 'apollo-boost';

export const QUERY_PATIENTS = gql`
    query {
        patients {
            firstName
            lastName
            age
            gender
            avatar
            contactInfo
            conditions {
                name
                severity
                startDate
                endDate
            }
        }
    }`;

export const CREATE_PATIENT = gql`
    mutation 
        (
            $firstName:String!,
            $lastName:String!,
            $contactInfo:String!,
            $age:Int!,
            $gender:String!,
            $conditions: [ConditionsInput]!
        ){
        createPatient (
            firstName:$firstName,
            lastName:$lastName,
            contactInfo:$contactInfo,
            age:$age,
            gender:$gender,
            conditions: {
                name:$name,
                severity:$severity,
                startDate:$startDate,
                endDate:$startDate
            }
        ){
            id  
            firstName
        }
    }
`;
