import { gql } from 'apollo-boost';

export const QUERY_PATIENTS = gql`
    query {
        patients {
            id
            firstName
            lastName
            age
            gender
            avatar
            contactInfo
            conditions {
                id
                name
                severity
                startDate
                endDate
            }
        }
    }`;

export const CREATE_PATIENT = gql`
mutation {
    createPatient(
      firstName:"Jen",
      lastName:"Horton",
      contactInfo:"chris.horton@sbcglobal.net",
      age:45,
      gender:"female",
      conditions: {
        name:"Lumbar issues",
        severity:5,
        startDate:"2017-01-22"
        endDate:"2020-03-20"
      }
    ) {
      firstName
    }
  }
`;
