import React from 'react';
import { useQuery, useMutation } from '@apollo/client';
import { QUERY_PATIENTS } from './queries.js'

export function PatientInfo() {

    const { data, loading } = useQuery(
        QUERY_PATIENTS
    );

    if (loading) return <p>Loading...</p>;

    return data.patients.map(({ id, firstName, lastName, age, contactInfo, gender, avatar }) => (
        <div key={id} class={id}>
            <p>
                Patient - {id}: {firstName} {lastName}
            </p>

            <button class="btn btn-primary" type="button" data-toggle={"#collapse"+id} data-target={"#collapse"+id} aria-expanded="false" aria-controls={"#collapse"+id}>
                Details
            </button>

            <div class={"#collapse"+id} id={"#collapse"+id}>
                <div class="card card-body">
                    <ul>
                        <li>Age: { age }</li>
                        <li>Gender: { gender }</li>
                        <li>Contact: { contactInfo }</li>
                    </ul>
                </div>
            </div>
        </div>
    ));
}
